mapper.update:
update t_cart
set num=#{num},
    modified_User=#{modifiedUser},
    modified_Time=#{modifiedTime}
where cid = #{cid}

mapper.select:
select *
from t_cart
where uid = #{uid}
  and pid = #{pid}

SELECT cid, uid, pid, t_product.title, t_product.image, t_cart.num, t_cart.price, t_product.price AS realPrice FROM t_cart LEFT JOIN t_product ON t_cart.pid = t_product.id WHERE cid IN (
#{cid}
) ORDER BY t_cart.created_time DESC
